public class Problem13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[] = {10,20,30,40,50};
		int sum=0;
		for(int i=0; i<arr.length; i++)
			sum = sum + arr[i];
		System.out.println("Sum of array = "+sum);
	}

}
