package org.jn.core.java.student.azhan.operators.ifelse;

public class Problem26 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean x = true;
        boolean y = false;
        if (x || y) {
            System.out.println(true);
        } else {
            System.out.println(false);
        }
	}

}
