package org.jn.core.java.student.dawood.akram.weekly.week1;

public class PreInc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
              int a = 99;
              System.out.println("'a' Value initially : a = "+a);
             ++a;
              int b = a;
              System.out.println("'b' value is : b = "+b);
              System.out.println("'a' value after PreInc : a = "+a);
              
	}

}
