public class FindNegOrPosOrZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num = 1;
       if(num>0) {
    	   System.out.println("Positive");
       } else if(num<0) {
    	   System.out.println("negative");
       } else {
    	   System.out.println("Zero");
       }
	}

}
