package org.jn.core.java.student.dawood.akram.weekly.week2;

public class FindOddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            int a = 1001;
            if(a%2==0) {
            	System.out.println("Even");
            } else {
            	System.out.println("Odd");
            }
	}

}
