package org.jn.core.java.student.dawood.akram.weekly.week3;

public class LoopBreaksAt7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int num=1;num<=10;num++) {
        	if(num==7) {
        		break;
        	}
        	System.out.println("Hello");
        }
	}

}
