package org.jn.core.java.student.mirza.baig.clss.collection;

import java.util.HashMap;

public class StringKeyStringValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		HashMap<String, String> map = new HashMap<>();  // key value pair
		
		
		map.put("mirza", "baig");
		map.put("virat", "kohli");
		map.put("rohit","sharma");
		map.put("kl", "rahul");
		map.put("mohammad", "shami");
		map.put("suryakumar", "yadav");
			
		System.out.println(map);

	}

}


