public class DivisibleByThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		 for (int number =1; number < 10; number ++) {
			 
			 if (number %3 ==0) {
				 
				 System.out.println(number);
				 
			 }
			 
		 }
	}

}
