package org.jn.core.java.student.mirza.baig.loop.pattern;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for (int i=4; i>=1; i--) {
			
			for (int j=1; j<=i; j++) {
			System.out.print("*");	
			}
			System.out.println();
		}
		}
				
	

		}
	


	


