package org.jn.core.java.student.mirza.baig.loop.pattern;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		
		for (int i =1; i<=2; i++) {
			
			// loop for space 
			for (int j=1; j<=i-i; j++) {
				System.out.print("");
			
		}
			for (int j=1; j<=i; j++) {
				
				System.out.print("*");
			}
			System.out.println();
	}
	}
}


