package org.jn.core.java.student.mirza.ifelse;

public class IfExample6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean x = true;
        boolean y = false;
        if (x || y) {
            System.out.println(true);
        } else {
            System.out.println(false);
        }

	}

}
