package org.jn.core.java.student.sayed.hassain.relationalOperator;

public class Program15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1, b = 2, c = -3;
		int s = ++a + b-- + c % 3; // a=2, b=1, c=-3, s=4

		System.out.println("ans : 2 actual :" + a);
		System.out.println("ans : 1 actual :" + b);
		System.out.println("ans : -3 actual :" + c);
		System.out.println("ans : 4 actual :" + s);
	}

}
