package org.jn.core.java.student.sayed.hassain.relationalOperator;

public class Program17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1, b = 2, c = -3;
		int s = a++ + b-- * c++; // a=2, b=1, c=-2, s=-5

		System.out.println("ans : 2 actual :" + a);
		System.out.println("ans : 1 actual :" + b);
		System.out.println("ans : -2 actual :" + c);
		System.out.println("ans : -5 actual :" + s);
	}

}
