public class CharIntCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//CharToIntExample
		
		char c = 'a';
		char c2 = '1';
		int a = c;
		int b = c2;
		System.out.println(a);
		System.out.println(b);
	}

}
