public class Program43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=14;
		if(num%7==0)
		{
			System.out.println(num+" is divisible by 7");
		}
		else {
			System.out.println(num+" is not divisible by 7");
		}

	}

}
