public class Program19 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 56;
        int b = -8;
        int c = a % b;		//a: 56   b: -8  c: 0
        int d = b % a;		//a: 56   b: -8  c: 0  d:-8
        System.out.println(c + ", " + d);

	}

}
