package org.jn.core.java.student.umer.faroque.arithmeticOperation;

/**
 * @author umerf
 *
 */
public class Program22 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;		
        a++;		//4 -> 5
        a += a;		//10
        --a;		//9
        a = 7 + a;	//16
        a *= a;		//256
        a -= 3;		//253
		System.out.println(" a = " + a );	//253
	}

}
