public class Program24 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -4;	
		a=a+1;        //-3
		a++;        //-3
		a+=1;        //-1
		++a; 		//0
		System.out.println(" a = " + a );//0
	}

}
