package org.jn.core.java.student.umer.faroque.datatype;

public class FloatTypeCast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		double f = 5.5;
		double pro = a * f;
		int mul = (int)(a * f);
		System.out.println("Pro: "+pro);
		System.out.println("mul: "+mul);
	}

}
