package org.jn.core.java.student.umer.faroque.logical_operation;

/**
 * @author umerf
 *
 */
public class Program21 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int c = 20;
        double kelvin = c + 273.15;//293.15
        System.out.println(kelvin);
        int Rankine = (int) (c + 273.15 * 9 / 5);//511
        System.out.println(Rankine);
        double delisle = (100 - c) * 3 / 2;//120.0
        System.out.println(delisle);
	}

}
