public class Program22 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double f = 70;
        double kelvin = (f + 459.67) * 5 / 9;//294.26
        System.out.println((int) kelvin);//294
        double newton = (f - 32) * 11 / 60;//6.96
        System.out.println((int) newton);//6
	}

}
