package org.la.core.java.student.datatype.SjMohammed;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float f= 3.14f;
		int i = 34;
		double d = 58.95;
		char c ='F';
		boolean b =true;
		System.out.println("Float and int Variable"+f +"and" +i);
		System.out.println("Float and double Variable"+f +"and" +d);
		System.out.println("Float and Char Variable"+f +"and" +c);
		System.out.println("Float and Boolean Variable"+f +"and" +b);

	}

}
