package org.la.core.java.student.datatype.kajamoideen.a2;

public class ArithmeticOperation_22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
        a++;
        a += a;
        --a;
        a = 7 + a;
        a *= a;
        a -= 3;
		System.out.println(" a = " + a );
	}

}
