package org.la.core.java.student.datatype.sjmohammed;

public class IntDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 34;
		long l=955;
		char c ='F';
		boolean b =true;
		byte bs=59;
		System.out.println("Integer and Long Variable"+i +"and" +l);
		System.out.println("Integer and char Variable"+i +"and" +c);
		System.out.println("Integer and boolean Variable"+i +"and" +b);
		System.out.println("Integer and Byte Variable"+i +"and" +bs);
	}

}
