package org.la.core.java.student.datatype.wang;

public class OctalBinaryHexaExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Octal base-8
		//Binary base_2
		//Hexademical base-16
		int decimal =110; //no prefix
		int binary=0b110; //'0b' is prefix
		int octal=0177;   //'0' is prefix
		int hexademical=0x12EF;  //0x is prefix
		
		System.out.println("deciaml "+ decimal);
		System.out.println("binary "+binary);
		System.out.println("octal "+ octal);
		System.out.println("hexademical "+hexademical);
		
	}

}
