package org.la.core.java.student.hasan.assignment02;

public class RelOp5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		boolean x = a++==10;
		boolean y = --a==10;
		System.out.println("a="+a);
		System.out.println("x="+x);
		System.out.println("y="+y);
		
	}

}
