public class DivisibilityBy3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=16;
		if (a%3==0)
			System.out.println(a+" is divisible by 3");
		else System.out.println(a+" is not divisible by 3");
	}

}
