public class DivisibilityBy7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=14;
		if (a%7==0)
			System.out.println(a+" is divisible by 7");
		else System.out.println(a+" is not divisible by 7");
	}

}
