package org.la.core.java.student.hasan.assignment03;

public class PostiveSingleOrDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=14;
		if (a>=0 && a<10)
			System.out.println(a+" is positive single digit");
		else if (a>=10 && a<100)
			System.out.println(a+" is positive double digit");
		else System.out.println(a+" is not postive/songle/double digit");
	}

}
