package org.la.core.java.student.hasan.assignment03;

public class TernaryDigit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		if (a>=100 && a<1000 || a<=-100 && a>-1000 )
			System.out.println(a+" is ternary digit");
		else System.out.println(a+" is not ternary digit");
	}

}
