public class ZeroPositiveNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9;
	    if (a>0)
	    	System.out.println("1");
	    else if(a<0)
	    System.out.println("-1");
	    else
	    	System.out.println("0");
	}

}
