package org.la.core.java.student.hasan.assignment04;

public class Loop52 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=10,sum=0;
		for (int i=1;i<=n;i++) {
			if(i%2==0) {
			System.out.print(i+" ");
			sum=sum+i;}
		}System.out.println("\nsum="+sum);
	}

}
