public class Loop56 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=12345;
int last=n%10;
while(n>=10) {
	n=n/10;
}System.out.println("sum of 1st and last digit="+n+"+"+last+"="+(n+last));
	}

}
