package org.la.core.java.student.hasan.assignment04;

public class Loop77 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 4; i >= 1; --i) {
		      for (int j = 1; j <= i; ++j) {
		        System.out.print("* ");
		      }
		      System.out.println();
		    }
	}

}
