package org.la.core.java.student.hasan.assignment04;

public class Loop83 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j, rows=3;           
		for (i = 1; i <= rows; i++)   
		{  
		for (j = 1; j <= i; j++)  
		{  
		System.out.print(i+" ");  
		}  
		System.out.println();  
		}  
	}

}
