package org.la.core.java.student.moshin.assignment2;

public class Que03PostPreIncreDecre07 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		// PRE AND POST INCREMENT DECREMENT IN DECCERENT OPRETOR IN ABOVE
		
		int a = 7;
		int b = --a;
		System.out.println("a = " + a + " b = " + b);
	
		int a1= 7;
		int b1 = a1--;
		System.out.println("a = " + a1 + " b = " + b1);
	
		int a2 = 12, b2 = --a2;
		System.out.println("a = " + a2 + " b = " + b2);
	
		int a3 = 89, b3 = a3--;
		System.out.println("a = " + a3 + " b = " + b3);
	
		int a4 = 9, b4 = --a4 * 5;
		System.out.println("a = " + a4 + " b = " + b4);
	
		int a5 = 9, b5 = a5-- * 5;
		System.out.println("a = " + a5 + " b = " + b5);
	
	}

}
