public class Example46 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 581, b = 1, c = 50;
        if (a > b) {
            if (a > c) {
                System.out.println(a + " is greatest");
            } else {
                System.out.println(c + " is greatest");
            }
        } else {
            if (b > c) {
                System.out.println(b + " is greatest");
            } else {
                System.out.println(c + " is greatest");
            }
        }
	}

}
