package org.la.core.java.student.sjmohammed.assignment4;

public class Example84 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=3;
		for(int x=1;x<=n;x++) {
			for(int y=1;y<=x;y++) {
				if(x==1 && y==1) {
					System.out.print(" "+y);
				}else{
				System.out.print(y);	
			}
			}
			System.out.println("");
		}
	}

}
