package org.la.core.java.student.sjmohammed.assignment4;

public class Example99 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 34;
        double d = 20.5;
        d+=a; //54.5
        a+=d;//88
        d-=a;//54.5-88=-33.5
        a-=d;//88-(-33.5)=121
        System.out.println(" d = " + ++d );//-32.5
        System.out.println(" a = " + a-- );//121
	}

}
