/*
 * wap to reverse an array
 */
public class Example16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[] = {5,45,54,55,8,54};
		for(int b=a.length-1;b>=0;b--) {
			System.out.println("a["+b+"] "+a[b]);
		}
	}

}
