public class ArithmeticOP23 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// follow the previous  illustration
		
		int a = 4;
        a -= 10; // a= -6
        a = -a; // a =  - -6 = +6 means a = 6
        a = 7 + a; //  a = 7+ 6 = 13
        System.out.println(" a = " + a );
        a %= a; // a % a, a = 13 % 13 = 0
        a = a * a - 3 ; // a = 0*0 - 3 = -3
        System.out.println(" a = " + a );

	}

}
