package org.la.core.java.student.waleed.homework.loop.assignment4;

public class Assign53SumOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
		int sum = 0;
		 int n =100;
			for(int i=1 ; i<=n ; i++) {
				if(i%2==1) {
				sum = sum + i;
		     }
			
				
	           }
			System.out.println("Sum = "+sum);
			
		
	}

}
